package org.sspart.task2;

public class Switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    String str="anki";
    switch (str) {
    case"anki":
    	System.out.println("it is anki");
    	break;
    case"raj":
    	System.out.println("it is raj");
    	break;
    case"gandla":
    	System.out.println("it is gandla");
    	break;
    default:
    	System.out.println("it is wrong input");
    	break;
    	}
	}

}
